package com.carter.leetcode;

import org.hamcrest.MatcherAssert;
import org.hamcrest.Matchers;
import org.junit.jupiter.api.Test;

class Item1745Test {

    @Test
    void checkPartitioning() {
        boolean rs = new Item1745().checkPartitioning("bcbddxy");
        MatcherAssert.assertThat(rs, Matchers.is(false));
    }

    @Test
    void checkPartitioning4() {
        boolean rs = new Item1745().checkPartitioning("juchzcedhfesefhdeczhcujzzvbmoeombv");
        MatcherAssert.assertThat(rs, Matchers.is(true));
    }

    @Test
    void checkPartitioning3() {
        boolean rs = new Item1745().checkPartitioning("abcbdd");
        MatcherAssert.assertThat(rs, Matchers.is(true));
    }

    @Test
    void checkPartitioning1() {
        boolean rs = new Item1745().checkPartitioning("acab");
        MatcherAssert.assertThat(rs, Matchers.is(false));
    }

    @Test
    void checkPartitioning2() {
        boolean rs = new Item1745().checkPartitioning1("");
        MatcherAssert.assertThat(rs, Matchers.is(false));
    }
}